        SELECT 'Eggs' as name, 'pack' as unit, 3 as q, 2.5 as price FROM dual UNION ALL
        SELECT 'Butter' as name, 'pcs' as unit, 1 as q, 3 as price FROM dual UNION ALL
        SELECT 'Cookies' as name, 'pcs' as unit, 1 as q, 2 as price FROM dual;

    lcImage clob;
    lbImage1 blob;
    lbImage2 blob;
    lbImage3 blob;
    lbImage4 blob;

    TYPE t_images IS TABLE OF pls_integer INDEX BY pls_integer;
    lrImages t_images;

    lnDok pls_integer;
    lnParagraph pls_integer;
    lnNum pls_integer;
    lnBreak pls_integer;
    lnHeader pls_integer;
    lnFooter pls_integer;
    lnTable pls_integer;
    lnCounter pls_integer := 1;
    lnSum number := 0;
    lrPage ZT_WORD.r_page;
    lnImage pls_integer;
    
    lbDokument blob;

    function decode_base64(p_clob_in in clob) return blob is
    v_blob blob;
    v_result blob;
    v_offset integer;
    v_buffer_size binary_integer := 48;
    v_buffer_varchar varchar2(48);
    v_buffer_raw raw(48);
    begin
        dbms_lob.createtemporary(v_blob, true);
        v_offset := 1;
        for i in 1 .. ceil(dbms_lob.getlength(p_clob_in) / v_buffer_size) loop
          dbms_lob.read(p_clob_in, v_buffer_size, v_offset, v_buffer_varchar);
          v_buffer_raw := utl_raw.cast_to_raw(v_buffer_varchar);
          v_buffer_raw := utl_encode.base64_decode(v_buffer_raw);
          dbms_lob.writeappend(v_blob, utl_raw.length(v_buffer_raw), v_buffer_raw);
          v_offset := v_offset + v_buffer_size;
        end loop;
        v_result := v_blob;
        dbms_lob.freetemporary(v_blob);
        return v_result;
    end decode_base64;


BEGIN
    --image definitions (encoded in base64 so that they can be stored in this script and then decoded in blob)
    
